import React, {useState} from "react";
import {GoogleMap, useLoadScript, Marker, InfoWindow, DirectionsService, DirectionsRenderer} from "@react-google-maps/api";
import * as POIData from "./POI-data-test.json";
import mapStyles from "../styles/mapStyles";

const libraries = ["places"];
const mapContainerStyle = {
    width: '100vw',
    height: '87.2vh',
};

const imageStyle = {
    alt: "img",
    float: 'left',
    width: 100
}

// const textStyle = {
//     textalign: 'left'
// }
const options = {
    styles: mapStyles,
    disableDefaultUI: true,
    zoomControl: true,
};
const center = {
    lat: 40.748440,
    lng: -73.985664,
};

function Map() {
    const[selectedPOI, setSelectedPOI] = useState(null);

    const {isLoaded, loadError} = useLoadScript({
        googleMapsApiKey: process.env.REACT_APP_GOOGLEMAP_API,
        libraries,
    });

    const mapRef = React.useRef();
    const onMapLoad = React.useCallback((map) => {
        mapRef.current = map;
    }, [])

    const panTo = React.useCallback(({lat, lng}) => {
        mapRef.current.panTo({lat, lng});
        mapRef.current.setZoom(14);
    }, [])
    
    if (loadError) return "Error";
    if (!isLoaded) return "Loading...";

    const directionsCallback = React.useCallback((res) => {
        console.log(res)
    
        if (res !== null) {
          if (res.status === 'OK') {
            setResponse(res)
          } else {
            console.log('response: ', res)
          }
        }
      }, [])

    const directionsServiceOptions = React.useMemo(() => {
    return {
        destination: destination,
        origin: origin,
        travelMode: travelMode,
    }
    }, [])

    const directionsRendererOptions = React.useMemo(() => {
    return {
        directions: response,
    }
    }, [])

    return (
        
        <div>
            <GoogleMap
                // id="map"
                mapContainerStyle={mapContainerStyle}
                zoom={10}
                center={center}
                options={options}
                // onClick={onMapClick}
                // onLoad={onMapLoad}
            >
                {POIData.features.map(POI => (
                    <Marker
                        key = {POI.properties.poiid}
                        position = {{
                            lat: POI.geometry.coordinates[1],
                            lng: POI.geometry.coordinates[0]
                        }}
                        onClick = {() => {
                            setSelectedPOI(POI);
                        }}
                        // icon = {{
                        //     url: " ",
                        //     scaledSize: new window.google.maps.Size(25, 25)
                        // }}
                    />
                ))}

                {selectedPOI && (
                    <InfoWindow
                        position = {{
                            lat: selectedPOI.geometry.coordinates[1],
                            lng: selectedPOI.geometry.coordinates[0]
                        }}
                        onCloseClick = {() => {
                            setSelectedPOI(null);
                        }}
                    >
                        <div>
                            <div >
                                <img 
                                    src={selectedPOI.properties.imageUrl}
                                    style = {imageStyle}
                                />
                            </div>
                            <div >
                                <b>{selectedPOI.properties.NAME}</b>
                                <br/>
                                Address: {selectedPOI.properties.ADDRESS}
                                <br/>
                                Description: {selectedPOI.properties.description}
                            </div>
                        </div>
                    </InfoWindow>
                )}

                {destination !== '' && origin !== '' && (
                    <DirectionsService
                    options={directionsServiceOptions}
                    callback={directionsCallback}
                    />
                )}

                {response !== null && (
                    <DirectionsRenderer options={directionsRendererOptions} />
                )}
            </GoogleMap>
        </div>
    );
}

export default Map
